pragma solidity ^0.4.24;

import "openzeppelin-solidity/contracts/token/ERC20/ERC20.sol";
import "openzeppelin-solidity/contracts/math/SafeMath.sol";
import "openzeppelin-solidity/contracts/math/Math.sol";

import "republic-sol/contracts/RepublicToken.sol";
import "republic-sol/contracts/DarknodeRegistry.sol";
import "republic-sol/contracts/DarknodeRewardVault.sol";

contract NodePool {
    using SafeMath for uint256;

    address constant public ETHEREUM = 0xEeeeeEeeeEeEeeEeEeEeeEEEeeeeEeeeeeeeEEeE;

    enum PoolStatus {
        None,
        Proposed,
        CanRegister,
        Registered,
        CanDeregister,
        Deregistered
    }

    struct Pool {
        address proposer;
        string name;
        uint8 operationFees;
        uint256 minimumDuration;
        uint256 totalAmountFunded;
        uint256 deregistrationVotes;
        address darknodeID;
        uint256 registeredAt;
    }

    bytes32[] public pools;
    mapping(bytes32 => Pool) public poolDetails;

    mapping(bytes32 => mapping(address => uint256)) private totalFees;
    mapping(bytes32 => mapping(address => mapping(address => uint256))) public collectedFees;

    uint8 constant public VOTES_FOR_DEREGISTRATION = 75;

    // Amount funded (indexed by [darknodeID][userAddress])
    mapping(bytes32 => mapping(address => uint256)) public amountFunded;
    mapping(bytes32 => mapping(address => bool)) public votingRecord;

    RepublicToken public ren;
    DarknodeRegistry public dnr;
    DarknodeRewardVault public rewardVault;

    constructor(
        RepublicToken _renAddress,
        DarknodeRegistry _dnrAddress,
        DarknodeRewardVault _rewardVaultAddress
    ) public {
        ren = _renAddress;
        dnr = _dnrAddress;
        rewardVault = _rewardVaultAddress;
    }

    modifier onlyWithStatus(bytes32 _poolID, PoolStatus status) {
        require(poolStatus(_poolID) == status, "invalid pool status");
        _;
    }

    modifier onlyWithStatuses(bytes32 _poolID, PoolStatus status1, PoolStatus status2) {
        PoolStatus actualStatus = poolStatus(_poolID);
        require(actualStatus == status1 || actualStatus == status2, "invalid pool status");
        _;
    }

    modifier notProposer(bytes32 _poolID) {
        require(poolDetails[_poolID].proposer != msg.sender, "not permitted for proposer");
        _;
    }

    modifier onlyProposer(bytes32 _poolID) {
        require(poolDetails[_poolID].proposer == msg.sender, "only permitted for proposer");
        _;
    }

    // TODO: Move description off-chain to save space/cost.
    function proposePool(bytes32 _poolID, string _name, uint8 _operationFees, uint256 _minimumDuration, uint256 _amount)
    external onlyWithStatus(_poolID, PoolStatus.None)
    {
        require(_operationFees <= 100, "operation fees must be a percentage");
        Pool memory pool = Pool({
            proposer: msg.sender,
            name: _name,
            operationFees: _operationFees,
            minimumDuration: _minimumDuration,
            totalAmountFunded: 0,
            deregistrationVotes: 0,
            darknodeID: 0x0,
            registeredAt: 0
        });
        pools.push(_poolID);
        poolDetails[_poolID] = pool;

        fundPool(_poolID, _amount);
    }

    function fundPool(bytes32 _poolID, uint256 _amount)
    public onlyWithStatus(_poolID, PoolStatus.Proposed)
    {
        address investor = msg.sender;

        uint256 remaining = minimumBond() - poolDetails[_poolID].totalAmountFunded;
        uint256 minimum = Math.min256(remaining, _amount);
        require(minimum > 0, "already fully funded");

        require(ren.transferFrom(investor, this, minimum), "amount trasfer failed");
        poolDetails[_poolID].totalAmountFunded = poolDetails[_poolID].totalAmountFunded.add(minimum);
        amountFunded[_poolID][investor] = amountFunded[_poolID][investor].add(minimum);
    }

    function withdrawFunds(bytes32 _poolID, uint256 _amount)
    external
    notProposer(_poolID)
    {
        address investor = msg.sender;
        PoolStatus status = poolStatus(_poolID);

        if (status == PoolStatus.Proposed || status == PoolStatus.CanRegister) {
            require(investor != poolDetails[_poolID].proposer, "proposer can't withdraw");
        } else if (status == PoolStatus.CanDeregister) {
            dnr.deregister(poolDetails[_poolID].darknodeID);
        } else if (status == PoolStatus.Registered || status == PoolStatus.None) {
            revert("invalid status");
        }

        poolDetails[_poolID].totalAmountFunded = poolDetails[_poolID].totalAmountFunded.sub(_amount);
        amountFunded[_poolID][investor] = amountFunded[_poolID][investor].sub(_amount);

        require(ren.transfer(investor, _amount), "amount trasfer failed");
    }

    function registerPool(bytes32 _poolID, address _darknodeID, bytes _publicKey)
    external
    onlyProposer(_poolID)
    onlyWithStatus(_poolID, PoolStatus.CanRegister)
    {
        uint256 amount = poolDetails[_poolID].totalAmountFunded;
        require(ren.approve(dnr, amount), "bond approval failed");
        dnr.register(_darknodeID, _publicKey, amount);
        poolDetails[_poolID].darknodeID = _darknodeID;
        poolDetails[_poolID].registeredAt = now;
    }

    function poolStatus(bytes32 _poolID) public view returns (PoolStatus) {
        if (poolDetails[_poolID].proposer == 0x0) {
            return PoolStatus.None;
        }

        if (poolDetails[_poolID].registeredAt == 0) {
            // Hasn't been registered yet

            return (poolDetails[_poolID].totalAmountFunded < minimumBond()) ?
                PoolStatus.Proposed :
                PoolStatus.CanRegister;
        } else {
            // Has been registered

            bool pendingRegistration = dnr.isPendingRegistration(poolDetails[_poolID].darknodeID);
            bool registered = dnr.isDeregisterable(poolDetails[_poolID].darknodeID);
            bool timePassed = now >= (poolDetails[_poolID].registeredAt + poolDetails[_poolID].minimumDuration);

            if (pendingRegistration || (registered && !timePassed)) {
                return PoolStatus.Registered;
            } else if (registered && timePassed) {
                return PoolStatus.CanDeregister;
            } else {
                return PoolStatus.Deregistered;
            }
        }
    }

    /// @notice Votes to deregister a darknode. The darknode will not be deregisterd
    /// until 75% of the contribution amount has opted to deregister.
    function voteToDeregister(bytes32 _poolID)
    external
    onlyWithStatus(_poolID, PoolStatus.Registered)
    {
        require(!votingRecord[_poolID][msg.sender], "already voted");
        poolDetails[_poolID].deregistrationVotes += amountFunded[_poolID][msg.sender];
        votingRecord[_poolID][msg.sender] = true;
        // require 75% of the votes to deregister to be achieved
        uint256 total = poolDetails[_poolID].totalAmountFunded - amountFunded[_poolID][poolDetails[_poolID].proposer];
        uint256 votedPercent = poolDetails[_poolID].deregistrationVotes * 100 / total;
        if (votedPercent >= VOTES_FOR_DEREGISTRATION) {
            dnr.deregister(poolDetails[_poolID].darknodeID);
        }
    }

    function minimumBond() public view returns (uint256) {
        return dnr.minimumBond();
    }

    function getTotalFees(bytes32 _poolID, ERC20 _token) public view returns (uint256) {
        address darknodeID = poolDetails[_poolID].darknodeID;
        uint256 feesInRewardVault = rewardVault.darknodeBalances(darknodeID, _token);
        uint256 feesInContract = totalFees[_poolID][_token];

        return feesInRewardVault + feesInContract;
    }

    function feeShare(bytes32 _poolID, ERC20 _token, address _withdrawer) public view returns (uint256) {
        uint256 percentage = ((amountFunded[_poolID][_withdrawer] * 100) / poolDetails[_poolID].totalAmountFunded);
        // Subtract fees

        if (_withdrawer == poolDetails[_poolID].proposer) {
            uint256 fromOthers = ((poolDetails[_poolID].totalAmountFunded - amountFunded[_poolID][_withdrawer]) * 100) / poolDetails[_poolID].totalAmountFunded;
            percentage = percentage + ((fromOthers * poolDetails[_poolID].operationFees) / 100);
        } else {
            percentage = (percentage * (100 - poolDetails[_poolID].operationFees)) / 100;
        }

        uint256 total = (getTotalFees(_poolID, _token) * percentage) / 100;
        uint256 collected = collectedFees[_poolID][_token][_withdrawer];
        uint256 share = total - collected;

        return share;
    }

    function withdrawFees(bytes32 _poolID, ERC20 _token) external {
        address darknodeID = poolDetails[_poolID].darknodeID;

        uint256 amount = rewardVault.darknodeBalances(darknodeID, _token);
        if (amount > 0) {
            rewardVault.withdraw(darknodeID, _token);
            totalFees[_poolID][_token] = totalFees[_poolID][_token].add(amount);
        }

        address withdrawer = msg.sender;
        uint256 share = feeShare(_poolID, _token, withdrawer);

        collectedFees[_poolID][_token][withdrawer] = collectedFees[_poolID][_token][withdrawer].add(share);

        if (address(_token) == ETHEREUM) {
            withdrawer.transfer(share);
        } else {
            require(_token.transfer(withdrawer, share), "token transfer failed");
        }
    }
}
